    _withdrawTokens(
      swapOutput,
      _data.bAssetPersonal[_output.idx],
      cachedBassetData[_output.idx],
      _recipient,
      maxCache
    );
    _data.bAssetData[_output.idx].vaultBalance =
      cachedBassetData[_output.idx].vaultBalance -
      SafeCast.toUint128(swapOutput);
