      uint256 kP = k;
      for (uint256 j = 0; j < len; j++) {
        kP = (kP * k) / (_x[j] * len);
      }
      kPrev = k;
      k =
        (((nA * _sum) / A_PRECISION + (kP * len)) * k) /
        (((nA - A_PRECISION) * k) / A_PRECISION + ((len + 1) * kP));
      if (_hasConverged(k, kPrev)) {
        return k;
      }
    }

    revert("Invariant did not converge");
  }

function _hasConverged(uint256 _k, uint256 _kPrev)
    internal
    pure
    returns (bool)
  {
    if (_kPrev > _k) {
      return (_kPrev - _k) <= 1;
    } else {
      return (_k - _kPrev) <= 1;
    }
  }

